import React, { Component } from "react";

export default class ProductDetail extends Component {
  render() {
    const { image, name, price, description, quantity } =
      this.props.selectedProduct;
    return (
      <div style={{
        backgroundColor: "rgba (0,0,0,0.5)",
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
      }} className='container card bg-white shadow rounded-lg p-4 w-75'>
        <span
          onClick={this.props.onHideDetail}
          style={{
            width: 40,
            height: 40,
            cursor: "pointer",
            position: "absolute",
            top: 10,
            right: 10,
          }}
          className="border rounded-circle d-flex justify-content-center align-items-center"
        >
          X
        </span>
        <div className="row">
          <div className="col-5">
            <img src={image} alt="" />
          </div>
          <div className="col-7">
            <h2>Name: {name}</h2>
            <h2>Price: ${price}</h2>
            <p>Description: {description}</p>
            <p>Inventory: {quantity}</p>
          </div>
        </div>
      </div>
    );
  }
}
